soup = BeautifulSoup(html, 'html.parser')

# Find all grantee elements
grantee_elements = soup.find_all(class_='grantee')

# Create a list to store the extracted data
data = []

# Extract grantee information
for grantee in grantee_elements:
    name = grantee.find(class_='title').get_text(strip=True)
    discipline_element = grantee.find(class_='discipline')
    discipline = discipline_element.get_text(strip=True) if discipline_element else ''
    location_element = grantee.find(class_='neighborhood')
    location = location_element.get_text(strip=True).replace('Location:', '') if location_element else ''
    data.append([name, discipline, location])

# Write the extracted data to a CSV file
with open('grantees.csv', 'w', newline='') as csvfile:
    writer = csv.writer(csvfile)
    writer.writerow(['Grantee', 'Discipline', 'Location'])  # Write header
    writer.writerows(data)
